import React, { lazy } from "react";
import "./Content.css";

// eslint-disable-next-line
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

const HomeView = lazy(() => import("../../views/Home/Home"));
const LoginView = lazy(() => import("../../views/CredentialForms/Login/Login"));
const RegisterView = lazy(() => import("../../views/CredentialForms/Register/Register"));

const AppContent = function () {
    //functions for handling forms
    const handleChange = function(e){
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    const handleSubmit = function(e,data,isLogin){
        e.preventDefault();
        const url = "http://localhost:9999/auth/" + (isLogin ? "login" : "register");
        fetch(url,{
            method: "post",
            body: JSON.stringify(data),
            headers: {"Content-Type": "application/json"}
        }).then(rawData => rawData.json());
    }

    return (
        <main>
            <Switch>
                <Route path="/" render={() => <HomeView />} exact />
                <Route path="/login" render={() => <LoginView handleSubmit={handleSubmit} handleChange={handleChange} />} exact />
                <Route path="/register" render={() => <RegisterView handleSubmit={handleSubmit} handleChange={handleChange} />} exact />
            </Switch>
        </main>
    );
}

export default AppContent;
