import React, { Suspense, Component } from 'react';
import "./App.css";

import AppHeader from "./components/Header/Header";
import AppContent from "./components/Content/Content";
import AppFooter from './components/Footer/Footer';
import Redirect from 'react-router-dom/Redirect';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      redirect: null,
      redirectPath: null,
      username: null,
    }

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleRedirect = this.handleRedirect.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  //functions for handling forms 
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  //NOTE: only for register and login
  handleSubmit(e, data, isLogin) {
    e.preventDefault();
    const url = "http://localhost:9999/auth/" + (isLogin ? "login" : "register");
    fetch(url, {
      method: "post",
      body: JSON.stringify(data),
      headers: { "Content-Type": "application/json" }
    }).then(rawData => rawData.json())
      .then(resBody => {
        if (resBody.username) {
          this.handleRedirect("/", {
            username: resBody.username
          });
        }
      });
  }

  handleLogout(){
    this.setState({
      username: null,
    });
  }

  handleRedirect(path, state) {
    if(state){
      this.setState({
        ...{
          redirectPath: path,
          redirect: true,
        }, ...state
      });
    }
    else{
      this.setState({
        redirect:true,
        redirectPath: path
      });
    }
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirectPath} />
    }
    return (
      <div className="App">
        <Suspense fallback={<span>Loading...</span>}>
          <AppHeader username={this.state.username} handleLogout={this.handleLogout} handleRedirect={this.handleRedirect} handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
          <AppContent handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
          <AppFooter />
        </Suspense>
      </div>
    );
  }

  componentDidUpdate() {
    if (this.state.redirect) {
      this.setState({
        redirect: false,
        redirectPath: null,
      })
    }
  }
}

export default App;
