import React, { Component } from 'react';
import AppHeader from "../AppHeader/AppHeader";
import AppContent from "../AppContent/AppContent";
import AppFooter from '../AppFooter/AppFooter';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      route: "home",
      user: null,
    }

    this.switchContentWithRoute.bind(this);
  }

  switchContentWithRoute(route) {
    if (route !== this.state.route) {
      this.setState({
        route: route
      });
    }
  }

  render() {
    return (
      <React.Fragment>
        <AppHeader switchContentWithRoute={this.switchContentWithRoute} />
        <AppContent />
        <AppFooter />
      </React.Fragment>
    );
  }
}

export default App;
