import React, { Suspense, Component } from 'react';
import "./App.css";

import AppHeader from "./components/Header/Header";
import AppContent from "./components/Content/Content";
import AppFooter from './components/Footer/Footer';
import Redirect from 'react-router-dom/Redirect';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username:null,
    }

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  //functions for handling forms
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit(e, data, isLogin) {
    e.preventDefault();
    const url = "http://localhost:9999/auth/" + (isLogin ? "login" : "register");
    fetch(url, {
      method: "post",
      body: JSON.stringify(data),
      headers: { "Content-Type": "application/json" }
    }).then(rawData => rawData.json())
      .then(resBody => {
        if (resBody.username) {
          this.setState({
            username: resBody.username
          });
          return <Redirect to="/"/>
        }
      });
  }

  render() {
    return (
      <div className="App">
        <Suspense fallback={<span>Loading...</span>}>
          <AppHeader username={this.state.username} handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
          <AppContent handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
          <AppFooter />
        </Suspense>
      </div>
    );
  }
}

export default App;
