import React, { Component } from "react";
import {Link} from "react-router-dom";
import "../loginregister.css";
import Redirect from "react-router/Redirect";

class LoginView extends Component {
    constructor(props){
        super(props);

        this.state = {
            username:null,
            password:null,
        }
    }

    render(){
        return (
            <div className="form">
                <form onSubmit={(e) => this.props.handleSubmit(e,this.state,true)}>
                    <div className="container">
                        <label htmlFor="username">
                            Username
                        </label>
                        <input onChange={this.props.handleChange.bind(this)} type="text" name="username" placeholder="Enter Email"/>
                        <label htmlFor="password">
                            Password
                        </label>
                        <input onChange={this.props.handleChange.bind(this)} type="password" name="password" placeholder="Enter Password"/>
                        <button onClick={() => <Redirect to="/"/>} type="submit" className="SubmitButton"> Login</button>
                    </div>
                    <div className="otherOption">
                        <p>Don't have an account? <Link to="/register">Register.</Link></p>
                    </div>
                </form>
            </div>
        );
    }
}

export default LoginView;
